import axios from 'axios';
import BASE_ADDR from '.';
var LOGIN = "".concat(BASE_ADDR, "/accounts/login");
var AUTH = "".concat(BASE_ADDR, "/accounts/profile");
var REFRESH = "".concat(BASE_ADDR, "/accounts/refresh");
export function loginAPI(data) {
  return axios.post(LOGIN, data);
}
export function authAPI(token) {
  return axios.get(AUTH, {
    headers: {
      Authorization: "Bearer ".concat(token.access)
    }
  });
}
export function refreshAPI(token) {
  return axios.post(REFRESH, {
    refresh: token.refresh
  });
}
export function forgotAPI() {
  return "".concat(BASE_ADDR, "/accounts/password/reset/");
}
export function resetAPI() {
  return "".concat(BASE_ADDR, "/accounts/password/reset/confirm/");
}
