import axios from 'axios';
import BASE_ADDR from '.';
var LOGIN = "".concat(BASE_ADDR, "/accounts/login");
var AUTH = "".concat(BASE_ADDR, "/accounts/profile");
var FORGOT = "".concat(BASE_ADDR, "/accounts/password/reset/");
var RESET = "".concat(BASE_ADDR, "/accounts/password/reset/confirm/");
export function loginAPI(data) {
  return axios.post(LOGIN, data);
}
export function authAPI(token) {
  return axios.get(AUTH, {
    headers: {
      Authorization: "Bearer ".concat(token)
    }
  });
}
export function forgotAPI(email) {
  return axios.post(FORGOT, email);
}
export function resetAPI(data) {
  return axios.post(RESET, data);
}
