import _ from 'lodash';
import { loginAPI, authAPI } from '../api/auth';
import { pageLoadingAction } from './page';
export var LOGIN_CLEAR = 'LOGIN_CLEAR';
export var LOGIN_SUCCESS = 'LOGIN_SUCCESS';
export var LOGIN_FAIL = 'LOGIN_FAIL';
export var LOGIN_CHECK = 'LOGIN_CHECK';
export var SET_AUTH = 'SET_AUTH';
export var LOGOUT = 'LOGOUT';
export function loginClearAction() {
  return {
    type: LOGIN_CLEAR
  };
}

function loginSuccessAction(token) {
  return {
    type: LOGIN_SUCCESS,
    payload: {
      token: token
    }
  };
}

function loginFailAction(errors) {
  return {
    type: LOGIN_FAIL,
    payload: {
      errors: errors
    }
  };
}

export function loginCheckerAction(fields) {
  return {
    type: LOGIN_CHECK,
    payload: {
      fields: fields
    }
  };
}
export function loginAction(username, password) {
  return function (dispatch, getState) {
    dispatch(pageLoadingAction(true));
    dispatch(loginClearAction());
    dispatch(loginCheckerAction({
      username: username,
      password: password
    }));

    if (_.isEmpty(getState().auth.errors)) {
      loginAPI({
        username: username,
        password: password
      }).then(function (res) {
        var data = res.data;

        if (data.status_code === 200) {
          dispatch(loginSuccessAction(data.access));
          dispatch(pageLoadingAction(false));
        } else {
          dispatch(loginFailAction(data.detail));
          dispatch(pageLoadingAction(false));
        }
      });
    } else {
      dispatch(pageLoadingAction(false));
    }
  };
}

function setAuthAction(auth) {
  return {
    type: SET_AUTH,
    payload: {
      auth: auth
    }
  };
}

export function authorizeAction(token) {
  return function (dispatch) {
    return authAPI(token).then(function (res) {
      if (res.data.status_code !== 200) {
        dispatch(setAuthAction(false));
        return false;
      }

      dispatch(setAuthAction(true));
      return true;
    });
  };
}
export function logoutAction() {
  return {
    type: LOGOUT
  };
}
