import React from 'react';
import { Button, Form, Header, Divider } from 'semantic-ui-react';
import Comment from "./comment";
import Comments from "/static/comments-test";

const CommentExampleMinimal = () => (
  <Comment.Group style={{ marginTop: '5rem' }}>
    <Divider horizontal>
      <Header as='h3'>نظرات</Header>
    </Divider>
    <Form reply>
      <Form.TextArea />
      <Button secondary positive style={{ marginBottom: '1rem' }}>
        ثبت نظر
      </Button>
    </Form>
    {}
  </Comment.Group>
);

export default CommentExampleMinimal;
