import _ from 'lodash';
import produce from 'immer';
import { LOGIN_CLEAR, LOGIN_LOAD, LOGIN_UNLOAD, LOGIN_FAIL, LOGIN_SUCCESS, LOGIN_CHECK, SET_TOKEN, LOGOUT } from '../actions/auth';
import initialState from '../store/initialState';

function loginClearReducer() {
  var state = arguments.length > 0 && arguments[0] !== undefined ? arguments[0] : initialState.auth;
  var action = arguments.length > 1 ? arguments[1] : undefined;
  return produce(state, function (draft) {
    draft.loading = false;
    draft.errors = {};
    return draft;
  });
}

function loginLoadReducer() {
  var state = arguments.length > 0 && arguments[0] !== undefined ? arguments[0] : initialState.auth;
  var action = arguments.length > 1 ? arguments[1] : undefined;
  return produce(state, function (draft) {
    draft.loading = true;
    return draft;
  });
}

function loginUnloadReducer() {
  var state = arguments.length > 0 && arguments[0] !== undefined ? arguments[0] : initialState.auth;
  var action = arguments.length > 1 ? arguments[1] : undefined;
  return produce(state, function (draft) {
    draft.loading = false;
    return draft;
  });
}

function loginSuccessReducer() {
  var state = arguments.length > 0 && arguments[0] !== undefined ? arguments[0] : initialState.auth;
  var action = arguments.length > 1 ? arguments[1] : undefined;
  return produce(state, function (draft) {
    var token = action.payload.token;
    draft.loading = false;
    draft.errors = {};
    draft.token = token;
    return draft;
  });
}

function loginFailReducer() {
  var state = arguments.length > 0 && arguments[0] !== undefined ? arguments[0] : initialState.auth;
  var action = arguments.length > 1 ? arguments[1] : undefined;
  return produce(state, function (draft) {
    var errors = action.payload.errors;
    draft.loading = false;
    draft.success = false;
    draft.errors = errors;
    return draft;
  });
}

function loginCheckerReducer() {
  var state = arguments.length > 0 && arguments[0] !== undefined ? arguments[0] : initialState.auth;
  var action = arguments.length > 1 ? arguments[1] : undefined;
  return produce(state, function (draft) {
    var fields = action.payload.fields;

    _.forEach(fields, function (value, key) {
      if (value === '') {
        draft.errors[key] = 'فیلد خالی است.';
      }
    });

    return draft;
  });
}

function setTokenReducer() {
  var state = arguments.length > 0 && arguments[0] !== undefined ? arguments[0] : initialState.auth;
  var action = arguments.length > 1 ? arguments[1] : undefined;
  return produce(state, function (draft) {
    var token = action.payload.token;
    draft.token = token;
    draft.loading = false;
    return draft;
  });
}

function logoutReducer() {
  var state = arguments.length > 0 && arguments[0] !== undefined ? arguments[0] : initialState.auth;
  var action = arguments.length > 1 ? arguments[1] : undefined;
  return produce(state, function (draft) {
    draft.token = {};
    draft.loading = false;
    return draft;
  });
}

function authReducers() {
  var state = arguments.length > 0 && arguments[0] !== undefined ? arguments[0] : initialState.auth;
  var action = arguments.length > 1 ? arguments[1] : undefined;

  switch (action.type) {
    case LOGIN_CLEAR:
      return loginClearReducer(state, action);

    case LOGIN_LOAD:
      return loginLoadReducer(state, action);

    case LOGIN_UNLOAD:
      return loginUnloadReducer(state, action);

    case LOGIN_FAIL:
      return loginFailReducer(state, action);

    case LOGIN_SUCCESS:
      return loginSuccessReducer(state, action);

    case LOGIN_CHECK:
      return loginCheckerReducer(state, action);

    case SET_TOKEN:
      return setTokenReducer(state, action);

    case LOGOUT:
      return logoutReducer(state, action);

    default:
      return state;
  }
}

export default authReducers;
