import React, { Component } from 'react';
import { Button, Modal, Header } from 'semantic-ui-react';

const Alert = ({ open }) => (
  <Modal basic size='fullscreen' open={open}>
    <Header as='h1' content='ثبت نظر' style={{ paddingBottom: '0', textAlign: 'center' }} />
    <Modal.Content>
      <p style={{ textAlign: 'center' }}>برای ثبت نظر باید در سایت وارد شوید</p>
    </Modal.Content>
    <Modal.Actions>
      <Button color='green' inverted style={{display: 'flex'}}>
        فهمیدم
      </Button>
    </Modal.Actions>
  </Modal>
);

export default Alert;
