import React from 'react';
import { Comment, Image } from 'semantic-ui-react';

const CommentComp = ({ author, date, content, isReply = false }) => {
  const marginRight = isReply ? '2rem' : 'auto';
  return (
    <Comment>
      <Comment.Content>
        <Comment.Author as='a' style={{ float: 'right' }}>
          {author}
        </Comment.Author>
        <Comment.Metadata>
          <span>{date}</span>
        </Comment.Metadata>
        <Comment.Text style={{ display: 'flex', justifyContent: 'flex-end' }}>
          {content}
        </Comment.Text>
        <Comment.Actions style={{ display: 'flex', justifyContent: 'flex-end' }}>
          <a>پاسخ دادن</a>
        </Comment.Actions>
      </Comment.Content>
    </Comment>
  );
};

export default CommentComp;
